CREATE TABLE `songs` (
  `songID` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) NOT NULL DEFAULT '',
  `aria` varchar(100) DEFAULT NULL,
  `onym` varchar(100) DEFAULT NULL,
  `arrangement` varchar(100) DEFAULT NULL,
  `song` text,
  `serial` smallint(6) NOT NULL DEFAULT '0',
  `recordID` int(11) NOT NULL DEFAULT '0',
  `isDyna` smallint(6) NOT NULL DEFAULT '0',
  `meno` text,
  `maleSinger` varchar(200) DEFAULT NULL,
  `fmaleSinger` varchar(100) DEFAULT NULL,
  `songUrl` varchar(200) DEFAULT NULL,
  `youtube` text,
  `levelID` int(11) NOT NULL DEFAULT '0',
  `nolyrics` smallint(6) NOT NULL DEFAULT '0',
  `checked` smallint(6) NOT NULL DEFAULT '0' COMMENT '1) 可公布 2)審核中 0)預設值',
  `fbID` varchar(50) DEFAULT NULL,
  `provideIP` varchar(30) DEFAULT NULL,
  `provideTime` datetime DEFAULT NULL,
  `videoID` varchar(20) DEFAULT NULL,
  `playTimes` int(11) NOT NULL DEFAULT '0',
  PRIMARY KEY (`songID`),
  KEY `fbID` (`fbID`)
) ENGINE=MyISAM AUTO_INCREMENT=2880 DEFAULT CHARSET=utf8;
